mapper.select:
select gift_no ,category_no ,category_item ,gift_name ,gift_content ,gift_price ,count ,img_path as "img_path" ,img_1 as "img_1" ,img_2 as "img_2" ,img_3 as "img_3"
from gift g left outer join product_img p on g.gift_no=p.product_no join gift_category using(category_no)
where count>0 and category_no = #{categoryNo} and status='Y'

mapper.insert:
		insert all	 
	 into gift(
		  		gift_no
		  	   ,category_no
		  	   ,gift_name
		  	   ,gift_content
		  	   ,gift_price
		  	   ,count
		  	   ,status
		  	   )
		values(
				'G'||seq_gbo.nextval
				,#{categoryNo}
				,#{giftName}
				,#{giftContent}
				,#{giftPrice}
				,#{count}
				,'Y'
			  )

      into product_img(
      					 img_no
      					,product_no
      					,img_1
      					,img_2
      					,img_3
      					,img_path
      				  )
      			values(
      					 seq_img.nextval
      					,'G'||seq_gbo.currval
      					,#{img1}
      					,#{img2}
      					,#{img3}
      					,#{imgPath}
      			)

      select 1 from dual

INSERT 
  into gift_review
     (
       GRIV_NO
     , GHIS_NO
     , GRIV_CONTENT
     , GRIV_GRADE
     )
values
     (
       seq_griv.nextval
     , #{ghisNo}
     , #{grivContent}
     , #{grivGrade}
     )

insert
  into gift_img
     (
       REVIEW_NO
     , G_ORIGIN_NAME
     , G_CHANGE_NAME
     , G_FILE_PATH
     )
values
     (
       seq_griv.CURRVAL
     , #{originName}
     , #{changeName}
     , #{filePath}
     )

mapper.update:
update gift
   set status = 'N'
 where gift_no = #{giftNo}
