mapper.update:
UPDATE member
   SET USER_NAME = #{userName}
     , BIRTHDAY = #{birthDay}
     , GENDER = #{gender}
     , PHONE = #{phone}
     , EMAIL = #{email}
 where USER_ID = #{userId}

mapper.select:
select count(*)
  from member
 where user_pwd = #{userPwd}

mapper.update:
update member
   set user_pwd = #{newPwd}
 where user_id = #{userId}

update member
   set status = 'N'
 WHERE user_id = #{userId}
  and USER_PWD = #{userPwd}

mapper.select:
		select count(*)
		from REVIEW
        join CONSULTING using (CS_NO)
		where PRODUCT_NO = #{productNo}
         and user_no = #{userNo}
